// App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import PostsPage from './PostsPage';
import NewPostPage from './NewPostPage';
import PostDetailPage from './PostDetailPage'; // Import the new component

function App() {
  return (
    <Router>
      <div>
        <Routes>
          <Route path="/newpost" component={NewPostPage} />
          <Route path="/viewposts" component={PostsPage} />
          <Route path="/post/:id" component={PostDetailPage} /> {/* Add this route */}
          <Route path="/" component={PostsPage} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
